import{_ as e}from"./chunks/mongo-y-echo-11.CaUgLze-.js";import{_ as a,c as r,ai as n,o as t}from"./chunks/framework.BrYByd3F.js";const s="/vitepress-blog-template/images/db/mongo/mongo-y-echo-4.png",l="/vitepress-blog-template/images/db/mongo/mongo-y-echo-10.png",g="/vitepress-blog-template/images/db/mongo/mongo-y-echo-9.png",i="/vitepress-blog-template/images/db/mongo/mongo-y-echo-8.png",m="/vitepress-blog-template/images/db/mongo/mongo-y-echo-6.png",d="/vitepress-blog-template/images/db/mongo/mongo-y-echo-7.png",c="/vitepress-blog-template/images/db/mongo/mongo-y-echo-5.png",p="/vitepress-blog-template/images/db/mongo/mongo-y-echo-1.png",h="/vitepress-blog-template/images/db/mongo/mongo-y-echo-2.png",b="/vitepress-blog-template/images/db/mongo/mongo-y-echo-3.png",C=JSON.parse('{"title":"Mongo入门 - MongoDB整体生态","description":"","frontmatter":{},"headers":[],"relativePath":"db/nosql-mongo/mongo-x-ecosystem.md","filePath":"db/nosql-mongo/mongo-x-ecosystem.md","lastUpdated":1737706346000}'),u={name:"db/nosql-mongo/mongo-x-ecosystem.md"};function f(B,o,D,k,M,_){return t(),r("div",null,o[0]||(o[0]=[n('<h1 id="mongo入门-mongodb整体生态" tabindex="-1">Mongo入门 - MongoDB整体生态 <a class="header-anchor" href="#mongo入门-mongodb整体生态" aria-label="Permalink to &quot;Mongo入门 - MongoDB整体生态&quot;">​</a></h1><blockquote><p>很多人在学习Mongo时仅仅围绕着数据库功能，围绕着CRUD和聚合操作，但是MongoDB其实已经基本形成了它自身的生态了。我们在学习一项技能时一定要跳出使用的本身，要从高一点的格局上了解整个生态，这样会对你构筑知识体系有很大的帮助。@pdai</p></blockquote><h2 id="整体生态" tabindex="-1">整体生态 <a class="header-anchor" href="#整体生态" aria-label="Permalink to &quot;整体生态&quot;">​</a></h2><p><img src="'+e+'" alt=""></p><h2 id="mongodb-server" tabindex="-1">MongoDB Server <a class="header-anchor" href="#mongodb-server" aria-label="Permalink to &quot;MongoDB Server&quot;">​</a></h2><p>MongoDB数据库，包含如下核心功能：</p><p><img src="'+s+'" alt=""></p><ul><li><a href="https://www.mongodb.com/try/download/community" target="_blank" rel="noreferrer">社区版本在新窗口打开</a></li></ul><p><img src="'+l+'" alt=""></p><ul><li><a href="https://www.mongodb.com/try/download/enterprise" target="_blank" rel="noreferrer">企业版本在新窗口打开</a></li></ul><p><img src="'+g+'" alt=""></p><h2 id="mongodb-cloud" tabindex="-1">MongoDB Cloud <a class="header-anchor" href="#mongodb-cloud" aria-label="Permalink to &quot;MongoDB Cloud&quot;">​</a></h2><p><a href="https://docs.mongodb.com/cloud/" target="_blank" rel="noreferrer">MongoDB Cloud在新窗口打开</a></p><p><img src="'+i+'" alt=""></p><h2 id="mongodb-drivers" tabindex="-1">MongoDB Drivers <a class="header-anchor" href="#mongodb-drivers" aria-label="Permalink to &quot;MongoDB Drivers&quot;">​</a></h2><p><a href="https://docs.mongodb.com/ecosystem/drivers/" target="_blank" rel="noreferrer">MongoDB Drivers在新窗口打开</a></p><p><img src="'+m+'" alt=""></p><p><a href="https://docs.mongodb.com/drivers/java/" target="_blank" rel="noreferrer">驱动相关的文档在新窗口打开</a></p><p><img src="'+d+'" alt=""></p><h2 id="工具相关" tabindex="-1">工具相关 <a class="header-anchor" href="#工具相关" aria-label="Permalink to &quot;工具相关&quot;">​</a></h2><p><a href="https://docs.mongodb.com/tools/" target="_blank" rel="noreferrer">MongoDB Tools在新窗口打开</a></p><p><img src="'+c+'" alt=""></p><h3 id="mongodb-atlas-open-service-broker" tabindex="-1">MongoDB Atlas Open Service Broker <a class="header-anchor" href="#mongodb-atlas-open-service-broker" aria-label="Permalink to &quot;MongoDB Atlas Open Service Broker&quot;">​</a></h3><p><a href="https://www.openservicebrokerapi.org/" target="_blank" rel="noreferrer">Open Service Broker在新窗口打开</a></p><p><a href="https://www.jdon.com/49640" target="_blank" rel="noreferrer">https://www.jdon.com/49640</a></p><h3 id="mongodb-connector-for-bi" tabindex="-1">MongoDB Connector for BI <a class="header-anchor" href="#mongodb-connector-for-bi" aria-label="Permalink to &quot;MongoDB Connector for BI&quot;">​</a></h3><p><a href="https://docs.mongodb.com/bi-connector/current/" target="_blank" rel="noreferrer">MongoDB Connector for BI在新窗口打开</a></p><p>BI是指Business Intelligence，众所周知的BI工具有Tableau, MicroStrategy和Qlik等；而MongoDB BI Connector充当在mongod或mongos实例与BI工具之间转换查询和数据的层， 这样用户就可以使用SQL创建查询，可视化，图形化和报告等方式来展示MongoDB中的数据。</p><p><img src="'+p+'" alt=""></p><ul><li><code>MongoDB DB</code>: 数据的存储</li><li><code>BI Connector</code>: 提供一个关系模式(Schema)，以及BI工具和MongoDB之间转换SQL查询</li><li><code>ODBC data source name (DSN)</code>: 连接配置数据和认证等.</li><li><code>BI Tool</code>: 数据分析和展示工具.</li></ul><h3 id="mongodb-charts" tabindex="-1">MongoDB Charts <a class="header-anchor" href="#mongodb-charts" aria-label="Permalink to &quot;MongoDB Charts&quot;">​</a></h3><p><a href="https://docs.mongodb.com/charts/saas/" target="_blank" rel="noreferrer">MongoDB Charts在新窗口打开</a></p><p>负责MongoDB数据可视化的一个工具。</p><p><img src="'+h+'" alt=""></p><h3 id="mongodb-command-line-interface-mongocli" tabindex="-1">MongoDB Command Line Interface (mongocli) <a class="header-anchor" href="#mongodb-command-line-interface-mongocli" aria-label="Permalink to &quot;MongoDB Command Line Interface (mongocli)&quot;">​</a></h3><p><a href="https://docs.mongodb.com/mongocli/stable/" target="_blank" rel="noreferrer">MongoDB Command Line Interface (mongocli)在新窗口打开</a></p><p>通过命令行进行管理Mongo的工具，支持Cloud和On-premises，Cloud通过MongoDB Atlas支持；On-premises通过 MongoDB Cloud Manager 和 MongoDB Ops Manager支持。</p><h3 id="mongodb-kubernetes-operator" tabindex="-1">MongoDB Kubernetes Operator <a class="header-anchor" href="#mongodb-kubernetes-operator" aria-label="Permalink to &quot;MongoDB Kubernetes Operator&quot;">​</a></h3><p><a href="https://github.com/mongodb/mongodb-kubernetes-operator" target="_blank" rel="noreferrer">MongoDB Community Kubernetes Operator在新窗口打开</a></p><p>将MongoDB部署到Kubernetes集群的工具。</p><h3 id="mongodb-compass" tabindex="-1">MongoDB Compass <a class="header-anchor" href="#mongodb-compass" aria-label="Permalink to &quot;MongoDB Compass&quot;">​</a></h3><p><a href="https://docs.mongodb.com/compass/current/" target="_blank" rel="noreferrer"><strong>MongoDB Compass</strong>在新窗口打开</a></p><p>提供的对MongoDB进行界面化管理的工具。</p><blockquote><p>需要注意：MongoDB 3.6+ 版本才支持这个工具；对于聚合操作的可视化是它的优势。</p></blockquote><h3 id="mongodb-vscode插件" tabindex="-1">MongoDB VsCode插件 <a class="header-anchor" href="#mongodb-vscode插件" aria-label="Permalink to &quot;MongoDB VsCode插件&quot;">​</a></h3><p><a href="https://docs.mongodb.com/mongodb-vscode/" target="_blank" rel="noreferrer">MongoDB for VS Code在新窗口打开</a></p><p>MongoDB VsCode插件</p><p><img src="'+b+'" alt=""></p><h3 id="mongodb-shell" tabindex="-1">MongoDB Shell <a class="header-anchor" href="#mongodb-shell" aria-label="Permalink to &quot;MongoDB Shell&quot;">​</a></h3><p><a href="https://docs.mongodb.com/mongodb-shell/" target="_blank" rel="noreferrer">MongoDB Shell在新窗口打开</a></p><p>mongosh是一个MongoDB的shell工具。</p><h3 id="mongodb-kafka-connector" tabindex="-1">MongoDB Kafka Connector <a class="header-anchor" href="#mongodb-kafka-connector" aria-label="Permalink to &quot;MongoDB Kafka Connector&quot;">​</a></h3><p><a href="https://docs.mongodb.com/kafka-connector/current/" target="_blank" rel="noreferrer">MongoDB Kafka Connector在新窗口打开</a></p><p>MongoDB连接kafka的工具。</p><h3 id="mongodb-spark-connector" tabindex="-1">MongoDB Spark Connector <a class="header-anchor" href="#mongodb-spark-connector" aria-label="Permalink to &quot;MongoDB Spark Connector&quot;">​</a></h3><p><a href="https://docs.mongodb.com/spark-connector/current/" target="_blank" rel="noreferrer">MongoDB Spark Connector在新窗口打开</a></p><p>MongoDB连接Spark的工具。</p><h3 id="mongodb-database-tools" tabindex="-1">MongoDB Database Tools <a class="header-anchor" href="#mongodb-database-tools" aria-label="Permalink to &quot;MongoDB Database Tools&quot;">​</a></h3><p><a href="https://docs.mongodb.com/database-tools/" target="_blank" rel="noreferrer">MongoDB Database Tools在新窗口打开</a></p><ul><li>二进制导入导出 <ul><li><code>mongodump</code> Creates a binary export of the contents of a mongod database.</li><li><code>mongorestore</code> Restores data from a mongodump database dump into a mongod or mongos</li><li><code>bsondump</code> Converts BSON dump files into JSON.</li></ul></li><li>数据导入导出 <ul><li><code>mongoimport</code> Imports content from an Extended JSON, CSV, or TSV export file.</li><li><code>mongoexport</code> Produces a JSON or CSV export of data stored in a mongod instance.</li></ul></li><li>诊断工具 <ul><li><code>mongostat</code> Provides a quick overview of the status of a currently running mongod or mongos instance.</li><li><code>mongotop</code> Provides an overview of the time a mongod instance spends reading and writing data.</li></ul></li><li>GridFS 工具 <ul><li><code>mongofiles</code> Supports manipulating files stored in your MongoDB instance in GridFS objects.</li></ul></li></ul><p>本文转自 <a href="https://pdai.tech" target="_blank" rel="noreferrer">https://pdai.tech</a>，如有侵权，请联系删除。</p>',61)]))}const S=a(u,[["render",f]]);export{C as __pageData,S as default};
