import{_ as a,c as t,ai as r,o as s}from"./chunks/framework.BrYByd3F.js";const x=JSON.parse('{"title":"工具详解 - Maven私有仓库Nexus","description":"","frontmatter":{},"headers":[],"relativePath":"tool/tool-maven-nexus.md","filePath":"tool/tool-maven-nexus.md","lastUpdated":1737706346000}'),n={name:"tool/tool-maven-nexus.md"};function l(o,e,i,c,u,h){return s(),t("div",null,e[0]||(e[0]=[r('<h1 id="工具详解-maven私有仓库nexus" tabindex="-1">工具详解 - Maven私有仓库Nexus <a class="header-anchor" href="#工具详解-maven私有仓库nexus" aria-label="Permalink to &quot;工具详解 - Maven私有仓库Nexus&quot;">​</a></h1><blockquote><p>Maven私有仓库Nexus介绍和使用, 工具的使用查查资料就可以，给几个链接减少下查找的时间。@pdai</p></blockquote><h2 id="nexus是什么" tabindex="-1">Nexus是什么 <a class="header-anchor" href="#nexus是什么" aria-label="Permalink to &quot;Nexus是什么&quot;">​</a></h2><p>1、有些公司都不提供外网给项目组人员，因此就不能使用maven访问远程的仓库地址，所以很有必要在局域网里找一台有外网权限的机器，搭建nexus私服，然后开发人员连到这台私服上，这样的话就可以通过这台搭建了nexus私服的电脑访问maven的远程仓库。而且自己maven私服更容易维护，由于在内网，公司的开发人员从maven私服迁出jar到本地仓库更快。</p><p>2、当需要上传第三方或者自己的jar到maven仓库时，就需要私服了。</p><h2 id="nexus安装" tabindex="-1">Nexus安装 <a class="header-anchor" href="#nexus安装" aria-label="Permalink to &quot;Nexus安装&quot;">​</a></h2><p><a href="https://blog.csdn.net/yjclsx/article/details/83992321" target="_blank" rel="noreferrer">https://blog.csdn.net/yjclsx/article/details/83992321</a></p><h2 id="nexus功能介绍" tabindex="-1">Nexus功能介绍 <a class="header-anchor" href="#nexus功能介绍" aria-label="Permalink to &quot;Nexus功能介绍&quot;">​</a></h2><p><a href="https://blog.csdn.net/yjclsx/article/details/83994535" target="_blank" rel="noreferrer">https://blog.csdn.net/yjclsx/article/details/83994535</a></p><h2 id="nexus配置和使用" tabindex="-1">Nexus配置和使用 <a class="header-anchor" href="#nexus配置和使用" aria-label="Permalink to &quot;Nexus配置和使用&quot;">​</a></h2><p><a href="https://blog.csdn.net/yjclsx/article/details/83996224" target="_blank" rel="noreferrer">https://blog.csdn.net/yjclsx/article/details/83996224</a></p><h2 id="nexus上传第三方jar包" tabindex="-1">Nexus上传第三方jar包 <a class="header-anchor" href="#nexus上传第三方jar包" aria-label="Permalink to &quot;Nexus上传第三方jar包&quot;">​</a></h2><p>上传含Maven依赖的jar包和源码包到Nexus并下载引入到其他项目中</p><ul><li><a href="https://www.jianshu.com/p/b8ec688c388e" target="_blank" rel="noreferrer">https://www.jianshu.com/p/b8ec688c388e</a></li><li><a href="https://blog.csdn.net/yjclsx/article/details/84027055" target="_blank" rel="noreferrer">https://blog.csdn.net/yjclsx/article/details/84027055</a></li></ul><p>本文转自 <a href="https://pdai.tech" target="_blank" rel="noreferrer">https://pdai.tech</a>，如有侵权，请联系删除。</p>',15)]))}const p=a(n,[["render",l]]);export{x as __pageData,p as default};
